	// 제이쿼리 이용 자동 스크롤 페이지 이동
	$(document).ready(function ($) {
    	$(".scroll").click(function (event) {
        	event.preventDefault();
			$('html,body').animate({
            	scrollTop: $(this.hash).offset().top
			}, 1500);
		});
	});

	// 제이쿼리 레이어 스라이딩 오픈 클로즈
	$(document).ready(function () {
    	$(".toggle").click(function () {
        	var id = $(this).attr("id");
        $("." + id + "_slide").slideToggle();
		});
	});



  // 테이블 위드값 계산
